import React, { useEffect, useState } from "react";
import axios from 'axios'
import { useParams } from "react-router-dom";
function Singleview() {
    const [data, setData] = useState("")
    const param = useParams()
    useEffect(() => {
        async function getData() {
            const result = await axios.get("http://localhost:8000/singlecourse?id=" + param.id)
            console.log(result.data.data.syllabus);
            setData(result.data.data)
           data.syllabus.forEach(e=>{
                console.log(e);
        })
        }
        getData()
    }, [param.id])
    return (
        <>
            <div className="container">
                <div className="row">
                    <div className="col-md-12">
                        <img src={data.thumbnail} className="singleimg" />
                        <div className="author">
                            <div className="authorData">
                                <span className="text-left"> Duration:</span><span>{data.duration}</span>
                            </div>
                            <div className="authorData">
                                <span className="text-left"> Location:</span><span>{data.location}</span>
                            </div>
                        </div>
                        <h3>{data.name} by {data.instructor}</h3>
                        <h5>Course Content</h5>
                        <div class="accordion" id="accordionExample">
                            {
                                data.syllabus.forEach(e => {
                          
                                        <>
                                            <div class="accordion-item">
                                                <h2 class="accordion-header">
                                                    <button class="accordion-button" type="button" data-bs-toggle="collapse" data-bs-target="#collapseOne" aria-expanded="true" aria-controls="collapseOne">
                                                        {e.topic}
                                                    </button>
                                                </h2>
                                                <div id="collapseOne" class="accordion-collapse collapse show" data-bs-parent="#accordionExample">
                                                    <div class="accordion-body">
                                                        <strong>{e.content}</strong> It is shown by default, until the collapse plugin adds the appropriate classes that we use to style each element. These classes control the overall appearance, as well as the showing and hiding via CSS transitions. You can modify any of this with custom CSS or overriding our default variables. It's also worth noting that just about any HTML can go within the <code>.accordion-body</code>, though the transition does limit overflow.
                                                    </div>
                                                </div>
                                            </div>
                                        </>
                                    
                                })
                            }

                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}
export default Singleview
